import React from "react";
const { useState } = React;

const Search = (props) => {
    const { onSearch } = props;
    const [Search, setSearch] = useState("");

    const onChange = (e) => {
        setSearch(e.target.value);
        if (e.target.value.length === 0) {
            onSearch(null);
        }
    };

    const onClick = async (e) => {
        onSearch(Search);
    };

    return (
        

    <div>
        <div className="tittle"> La Pokedex Más Completa</div>
    <div className="searchbar-container">
      <div className="searchbar">
        <input className="input-css" placeholder="¿Qué pokemon quieres conocer?" onChange={onChange} />
      </div>
      <div className="search-click">
        <button className="search-btn" onClick={onClick}>Buscar</button>
      </div>
    </div>
    </div>
    );
};

export default Search;
